Select * from (select localc, modelocor, sum(EstoqueEmMaos) as EEM, SUM(EstoqueEmtransito) as EET, SUM(unidadesVendidasLiquido) as Vendas, SUM(EstEMCD) + SUM(ESTETCD) as EstCD, Max(CampeaoGlobal) as Camp, sum(AlvoAtual) as Alvo, MAX(DiasComEstoque) as MaDCE, MAX(DiasComEstoque360) as MaDCE360, SUM(unidadesVendidasLiquido360) as Vend360, MIN(DiasComEstoque) as MiDCE, MAX(DataCadastroAlvo) as MaDDCA, Min(DataCadastroAlvo) as MiDDCA FROM evalcomcdn where Colecao = 'OI19' and localc <>'0-CD' group by localc, modelocor) base WHERE (EEM > 0 or EET > 0)  AND ((MaDCE > 14 and Vendas < 2) OR (MADCE > 29 and Vendas < 3) or (MaDCE360 > 44 and Vend360 < 4)) and ESTCD < 15